class Solution {
public:
    int maxTotalReward(vector<int>& rewardValues) {
        bitset<100100> b, now;
        b.set(0);
        sort(rewardValues.begin(),rewardValues.end());
        for (int r : rewardValues) {
            now.set();
            now = now >> (100100-r);
            now = b & now;
            now = now << r;
            b = b | now;
        }
        for (int i = 100099; i >= 0; --i) {
            if (b[i]) return i;
        }
        return -1;
    }
};
