class Solution {
public:
    int maxTotalReward(vector<int>& a) {
        int n = a.size();
        const int MX = 1e5 + 10;
        //const int MX = 20;
        using B = bitset<MX>;

        int T = MX - 1;

        B st = 0;
        st[T] = 1;
        sort(a.begin(), a.end());
        int x = 0, ans = 0;
        int j = 0;
        vector<int> ev(MX, 0);

        B pw;
        pw[T] = 1;
        for (int i : a) {
            while (j < i) {
                pw[T - j] = 1;
                j++;
            }
            B nxt = (st & pw) >> i;
            ans = max<int>(ans, T - nxt._Find_first());
            st |= nxt;
        }
        return ans;
    }
};
