const int I = 50001;
class Solution {
public:
    int maxTotalReward(vector<int>& v) {
        bitset<I> a, b;
        vector<int> s(I);
        int mx = 0;
        for (auto i : v) s[i] = 1;
        b[0] = 1;
        a[0] = 1;
        for (int i = 1; i < I; i++) {
            if (s[i]) {
                b |= (b & a) << i;
                mx = i;
            }
            a[i] = 1;
        }
        for (int i = mx - 1; i >= 0; i--) {
            if (b[i]) return i + mx;
        }
        return 622;
    }
};
