const int M = 25;
const int MX = (1<<M)-1;
int C[100000/M + 100];

class Solution {
public:
  int maxTotalReward(vector<int>& A) {
    sort(A.begin(), A.end());
    memset(C, 0, sizeof(C));

    C[0]=1;

    int best = 0;
    for(int i = 0; i < A.size(); ++i) {
      int ds = A[i]/M;
      int es = A[i]%M;
      int x = 0;
      for (; x*M + M <=A[i]; ++x) {
        C[x+ds]|=(C[x]<<es)&MX;
        C[x+ds+1]|=(C[x]>>(M-es))&MX;
      }
      int r = A[i]-x*M;
      if (r) {
        int w = C[x]&((1<<r) - 1);
        C[x+ds]|=(w<<es)&MX;
        C[x+ds+1]|=(w>>(M-es))&MX;
      }

    }
    for (int i = 100000/M + 100-1; i>=0; --i) {
      if (C[i]) {
        int j = M-1;
        while(!(C[i]>>j&1))--j;
        return i*M+j;
      }
    }
    return -1;
  }
};
