class Solution {
public:
    int maxTotalReward(vector<int>& a) {
        int n = (int) a.size();
        std::sort(a.begin(), a.end());
        constexpr int LIM = 1E5 + 5;
        std::bitset<LIM> f;
        int cur = 0;
        std::bitset<LIM> dp, ndp;
        dp[0] = 1;
        for (int i = 0; i < n; ++i) {
            while (cur < a[i]) {
                f[cur] = 1;
                ++cur;
            }
            ndp = dp;
            ndp |= ((dp & f) << a[i]);
            std::swap(dp, ndp);
        }
        int ans = 0;
        for (int i = LIM - 1; i >= 0; --i) {
            if (dp[i]) {
                ans = i;
                break;
            }
        }
        return ans;
    }
};
