class Solution {
	public int maxTotalReward(int[] a) {
		Arrays.sort(a);
		long[] b = new long[(50005 >>> 6) + 1];
		b[0] = 1;
		int ans = 0;
		for (int v : a) {
			for(int i = v-1;i >= v/64*64;i--){
				if(b[i>>>6]<<~i<0){
					if(i+v >= 50005){
						ans = Math.max(ans, i+v);
					}else {
						b[i + v >>> 6] |= 1L << i + v;
					}
				}
			}
			int shift = v;
			int sb = shift>>>6;
			if ((shift & 63) != 0) {
				for(int i = v/64-1;i >= 0;i--){
					if (i + sb + 1 < b.length) {
						b[i + sb + 1] |= b[i] >>> -shift;
					}
					if(i + sb < b.length){
						b[i + sb] |= b[i] << shift;
					}
					if(b[i] != 0){
						int x = Long.numberOfTrailingZeros(Long.highestOneBit(b[i])) + i*64;
						ans = Math.max(ans, x+v);
					}
				}
			}else{
				for(int i = v/64-1;i >= 0;i--){
					if(i+sb < b.length){
						b[i + sb] |= b[i];
					}
					if(b[i] != 0){
						int x = Long.numberOfTrailingZeros(Long.highestOneBit(b[i])) + i*64;
						ans = Math.max(ans, x+v);
					}
				}
			}
		}
		for(int i = b.length-1;i >= 0;i--){
			if(b[i] != 0){
				int x = Long.numberOfTrailingZeros(Long.highestOneBit(b[i])) + i*64;
				ans = Math.max(ans, x);
			}
		}
		return ans;
	}
}
