class Solution {
public:
    int maxTotalReward(vector<int>& rewardValues)
{
    vector<int>& R = rewardValues;
    sort(R.begin(), R.end());
    R.resize(unique(R.begin(), R.end()) - R.begin());
    int n = (int)R.size();
    bitset<50000> vis, sh;
    for(int i = 0; i < n - 1; i++) sh.set(R[i]);
    vis.set(0);
    int ans = 0;
    for(int i = 0; i < R.back(); i++)
    {
        if(sh[i]) sh[i] = 0;
        if(vis[i])
        {
            vis |= sh << i;
            ans = R.back() + i;
        }
    }
    return ans;
}
};
