const int M = 4e5 + 1;

typedef bitset<M> BS;

class Solution {
public:
    int maxTotalReward(vector<int>& a) {
        sort(a.begin(), a.end());
        a.erase(unique(a.begin(), a.end()), a.end());
        int n = a.size();

        BS cur;
        cur[0] = 1;

        BS msk;
        int p = 0;

        for (int i : a) {
            while (p < i) {
                msk[p] = 1;
                p++;
            }
            cur |= (cur & msk) << i;
            cur[i] = 1;
            // cout << i << ' ' << msk << ' ' << cur << endl;
        }

        int ans = a[n - 1];
        for (int i = a[n - 1] - 1; i >= 0; i--) {
            if (cur[i]) {
                ans = i + a[n - 1];
                break;
            }
        }

        return ans;
    }
};
