class Solution {
public:
    int maxTotalReward(vector<int>& rewardValues) {
        bitset<101010>bi;
        bi[0]=1;
        auto v=rewardValues;
        sort(begin(v),end(v));
        bitset<101010>ma;ma.set();
        for(int i:v)
        {
            bi|=(bi&(ma>>(101010-i)))<<i;
            //for(int j=0;j<30;j++)cout<<ma[j];
            //cout<<endl;
            //for(int j=0;j<30;j++)cout<<bi[j];
            //cout<<endl;
        }
        int ans=0;
        for(int i=0;i<101010;i++)if(bi[i]==1)ans=i;
        return ans;
    }
};
