class Solution {
public:

    int maxTotalReward(vector<int>& rewardValues) {
        bitset<100001> bit, cur;
        bit.set(0);
        int ans = 0;
        sort(rewardValues.begin(), rewardValues.end());
        for(int x: rewardValues){
            cur.set();
            cur = (cur << (100000-x+1)) >> (100000-x+1);
            bit |= ((bit&cur) << x);
        }
        for(int i = 100000; i >= 0; i --){
            if(bit[i]){
                ans = i;
                break;
            }
        }
        return ans;
    }
};
