from sortedcontainers import SortedSet
class Solution:
    def maxTotalReward(self, rewardValues: List[int]) -> int:
        rewardValues = sorted(set(rewardValues))
        mx = rewardValues[-1]
        if len(rewardValues) == 1: return mx
        elif rewardValues[-2] == mx - 1: return 2 * mx - 1

        poss = SortedSet([0])
        res = 0
        for num in rewardValues:
            i = poss.bisect_left(num) - 1
            if i >= 0:
                res = max(res, num + poss[i])
            add_lst = []
            for num2 in poss:
                if num2 >= num or num + num2 >= mx: break
                if num + num2 == mx - 1:
                    return 2 * mx - 1
                add_lst.append(num + num2)
            for num2 in add_lst:
                poss.add(num2)
        return res
