    const int MX=1e5+5;
class Solution {
public:

    int maxTotalReward(vector<int>& a) {
        bitset<MX> bs;
        bs[0]=1;
        sort(a.begin(), a.end());
        for (int i : a) {
            bitset<MX> mask;
            mask.set();
            mask >>= MX - i;
            bitset<MX> smaller = bs & (mask);
            bs = bs | (smaller << i);
        }
        int ans=0;
        for (int i=0; i<MX; ++i)
            if (bs[i])
                ans=i;
        return ans;
    }
};
