import java.math.BigInteger;

class Solution {

    public int maxTotalReward(int[] rewardValues) {
        TreeSet<Integer> set = new TreeSet<>();
        for (int rewardValue : rewardValues) {
            set.add(rewardValue);
        }
        BigInteger integer = BigInteger.ONE;
        for (int i : set) {
            integer = BigInteger.ONE.shiftLeft(i).subtract(BigInteger.ONE).and(integer).shiftLeft(i).or(integer);
        }
        return integer.bitLength() - 1;
    }
}
