class Solution {
public:
    int maxTotalReward(vector<int>& a) {
        int n = a.size();
        int m = *max_element(a.begin(), a.end());
        vector<bool> dp(2 * m + 5);
        sort(a.begin(), a.end());

        set<int> s;
        for(int i = 0; i < n; i++) s.insert(a[i]);

        long long l = s.size();
        if(l >= 100 && l * l > (long long)m){
            return 2 * m - 1;
        }

        dp[0] = true;
        int res = 0, last = -1;
        for(int i = 0; i < n; i++){
            int x = a[i];
            if(x == last) continue;
            for(int j = x - 1; j >= 0; j--){
                if(dp[j]){ 
                    dp[j + x] = true;
                    res = max(res, j + x);
                }
            }
            last = x;
        }
        return res;
    }
};
