template<typename T>
void asort(vector<T> &a) {sort(a.begin(), a.end());}

template<typename T>
vector<T> get_unique(vector<T> a) {
    asort(a);
    a.erase(unique(a.begin(), a.end()), a.end());
    return a;
}

using ll = long long;
using vi = vector<ll>;
using vvi = vector<vi>;
using pii = pair<ll,ll>;
using vp = vector<pii>;
using vvp = vector<vp>;
using vs = vector<string>;
using vvs = vector<vs>;
using ti3 = tuple<int,int,int>;
using vti3 = vector<ti3>;

class Solution {
public:
    int maxTotalReward(vector<int>& b) {
        vi a(b.begin(), b.end());
        a = get_unique(a);  
        ll n = a.size();
        using bt = bitset<50001>;
        bt dp;
        dp.set(0);
        for (auto x : a) {
            bt y;
            y.set();
            y >>= (50000-(x-1));
            y &= dp;
            dp |= (y<<x);
            // cout << dp << endl;
            // cout << (y<<x) << endl;
            // cout << dp << endl;
            // cout << endl;
        }
        ll ans = 0;
        set<ll> s;
        for (ll i=0; i<=50000; i++) {
            if (dp[i]) {
                ans = max(ans, i);
                s.insert(i);
            }
        }
        for (auto x : a) {
            auto it = s.lower_bound(x);
            it--;
            ans = max(ans, x + *it);
        }
        return ans;

    }
};
