class Solution {
public:
    int maxTotalReward(vector<int>& R) {
        sort(R.begin(), R.end());
        const int nax = 1e5 + 10;
        bitset<nax> bset;

        bset[0] = 1;
        for(int i: R){
            bitset<nax> mask(bset);
            mask <<= (nax - i);
            mask >>= (nax - i);
            auto temp = bset & mask;
            bset = bset | (temp << i);
        }
        for(int i = nax - 1; i >= 0; --i){
            if(bset[i]){
                return i;
            }
        }
        return -1;
    }
};
