#include<bits/stdc++.h>
using namespace std ; 
typedef long long int64 ; 

const int M = 2 * 5*1e4 + 1 ; 
const int N = M ; 

class Solution {
public:
    int maxTotalReward(vector<int>& rewardValues) {

        bitset<M> s ; 
        s[0] = 1 ; 


        set<int> alls ; 
        for( auto x : rewardValues ) {
            alls.emplace( x ) ; 
        }
        int max_val = *( --alls.end() ) ; 

        for( auto x : alls )  { 
            int m = x ; 
            std::bitset<N> mask = ~((~std::bitset<N>(0)) << m);
            auto add = s & mask ; 
            add = add << x ; 

            s |= add ; 
        }

        int ans = 0 ; 
        for( int y = 2 * max_val ; y >= 0 ; y-- ) { 
            if( s[y] ) {
                ans = max( ans , y ) ; 
                break ;
            }
        }

        return ans ; 
    }
};


