const int maxa = 1e5+3;

class Solution {
public:
    int maxTotalReward(vector<int>& rewardValues) {
        auto &a = rewardValues;
        sort(a.begin(), a.end());

        bitset<maxa> dp;
        dp[0] = 1;
        for (int x : a) {
            dp |= ((dp << (maxa-x)) >> (maxa-2*x));
        }

        for (int i=maxa-1; i>=0; i--) {
            if (dp[i]) return i;
        }
        return -1;
    }
};
