class Solution {
public:
    int maxTotalReward(vector<int>& a) {
        sort(a.begin(),a.end());
        a.resize(distance(a.begin(),unique(a.begin(),a.end())));
        int ret=0;
        set<int> dp;
        dp.insert(0);
        for(auto &i:a){
            auto itr=dp.lower_bound(i);
            if(itr!=dp.begin()){
                itr--;
                ret=max(ret,*itr+i);
            }
            itr=dp.lower_bound(min(i,a.back()-i));
            while(itr!=dp.begin()){
                itr--;
                if(dp.count(*itr+i)) break;
                dp.insert(*itr+i);
            }
        }
        return ret;
    }
};
