class Solution {
public:
    int maxTotalReward(vector<int>& rewardValues) {

        sort(rewardValues.begin(),rewardValues.end());

        int n=rewardValues.size();

        bitset<100000> b1;
        b1.set(0);
        bitset<100000> zeroingout;
        int pos=0;

        for(auto&x:rewardValues)
        {
            while(pos<x)
            {
                zeroingout.set(pos);
                pos++;
            }
            b1 |= ((b1&zeroingout)<<x);
        }

        int ans=0;
        for(int i=0;i<100000;i++)
        {
            if(b1.test(i))   ans=i;
        }
        return ans;
    }
};
