class Solution:
    def maxTotalReward(self, a: List[int]) -> int:
        a = list(sorted(set(a)))
        n = len(a)
        dp = 1
        last = a[n - 1]
        last_mask = (1 << last) - 1
        for i in range(n - 1):
            dp = dp | (((dp & ((1 << a[i]) - 1)) << a[i]) & last_mask)
        for i in reversed(range(last)):
            if (dp >> i) & 1:
                return i + a[n - 1]
