class Solution {
public:
    int maxTotalReward(vector<int>& rewardValues) {
        sort(rewardValues.begin(), rewardValues.end());

        const int nmm = 1e5+1;

        bitset<nmm>dp;
        bitset<nmm>tmp1, tmp2;
        int ptr = 0;

        dp[0] = 1;

        for(auto u:rewardValues) {

            while(ptr < 2*u) {
                tmp1[ptr] = 1;
                ptr++;
            }

            tmp2 = dp|tmp1;

            dp = dp|(dp<<u);
            dp = dp&tmp2;
        }

        for(int i=nmm-1;i>=0;i--) {
            if(dp[i]) return i;
        }

        return 0;
    }
};
