class Solution {
public:
    int maxTotalReward(vector<int>& a) {
        sort(a.begin(), a.end());
        int n = a.size();

        constexpr int m = 50005;
        bitset<m> b;
        b[0] = 1;
        for (int i : a) {
            b |= ((b<<(m-i))>>(m-i)) << i;
        }

        int ans = 0;
        for (int i = m-1; i >= 0; --i) {
            if (b[i]) {
                if (a.back() > i)
                    ans = max(ans, i+a.back());
                ans = max(ans, i);
            }
        }
        return ans;
    }
};
