typedef long long ll;

#define pb push_back
#define sz(x) (int)x.size()
#define all(x) begin(x),end(x)
#define lb(x,y) lower_bound(all(x),y)-begin(x)
class Solution {
public:
    int maxTotalReward(vector<int>& arr) {
        int mx = 0;
        set<int> st;
        for (int i : arr) mx = max(mx, i), st.insert(i);
        bitset<100001> dp, dp2;
        dp[0] = 1;
        for (int i = 1; i <= mx; i++) {
            dp2[i - 1] = dp2[i - 1] || dp[i - 1];
            if (st.count(i)) dp |= dp2 << i;
        }
        for (int i = 100000; i >= 0; i--) {
            if (dp[i]) return i;
        }
        return -1;
    }
};
