#include <bits/stdc++.h>
using namespace std;

#define FOR(i, x, y) for (int i = x; i < y; i++)
#define ld long double
#define ll long long
#define pii pair<int, int>
#define pll pair<ll, ll>

const int sz = 100001;

bitset<sz> dp;

class Solution {
public:
    int maxTotalReward(vector<int>& A) {
        sort(A.begin(), A.end());
        A.resize(unique(A.begin(), A.end()) - A.begin());

        int n = A.size();
        int mx = A.back();

        dp[0] = true;

        for (int i = 0; i < n; i++){
            // for (int sum = A[i] - 1; sum >= 0; sum--){
                int off = sz - A[i];

                if (off >= A[i]){
                    dp |= ((dp << off) >> (off - A[i]));
                }
                else{
                    dp |= ((dp << off) << (A[i] - off));
                }
                // dp |= (((dp << off) >> off) << A[i]);
            // }
        }
        int ans = 0;

        for (int i = 0; i <= mx * 2; i++){
            if (dp[i])
                ans = i;
            dp[i] = false;
        }    
        return ans;
    }
};
