class Solution {
public:
    int maxTotalReward(vector<int>& r) {
         bitset <100001> a,at;
        int st = -1;
        a[0] = 1;
        sort (r.begin(), r.end());
        for (int i = 0; i < r.size(); i ++) {
            int nm = r[i];
            while (st + 1 < nm) {
                at[st + 1] = 1;
                st++;
            }
            bitset <100001> t;
            t = a;
            t = (t & at);
            a |= (t << nm);
        }

        for (int i = 100000; i>=0; i--) {
            if (a[i])return i;
        }
        return 0;
    }
};
