class Solution {
public:
    int maxTotalReward(vector<int>& rewardValues) {
        vector<int> a=rewardValues;
        sort(a.begin(),a.end());
        const int N=100000;
        bitset<N+1> bs,b1;
        bs[0]=1;
        b1[0]=1;
        int p=0;
        for(auto x:a){
            while(p+1<x){
                p++;
                b1[p]=1;
            }
            bs|=((b1&bs)<<x);
        }
        int ans=0;
        for(int i=0;i<=N;i++) if(bs[i]==1) ans=i;
        return ans;
    }
};
