class Solution {
public:
    int maxTotalReward(vector<int>& rewardValues) {
        sort(rewardValues.begin(), rewardValues.end());
        // int b = rewardValues.back();
        bitset<50000> bs;
        bs[0] = 1;
        for (int i = 0; i < rewardValues.size() - 1; i++) {
            bs = bs | ((bs << (50000 - rewardValues[i]) >> (50000 - rewardValues[i])) << rewardValues[i]);
        }
        int res = rewardValues.back();
        for (int i = res - 1; i > -1; i--) {
            if (bs[i]) return i + res;
        }
        return res;
    }
};
