class Solution {
public:
    int maxTotalReward(vector<int>& v) {
        sort(v.begin(),v.end());
        int n=v.size();
        int mx=v[n-1];
        vector<int> dp(mx,0);
        dp[0]=1;
        for(int i=0;i<n;i++){
            if(v[i]==mx){
                break;
            }
            int j=min(2*v[i]-1,mx-1);
            for(;j>0;j--){
                if(j-v[i]<0){
                    break;
                }
                if(dp[j-v[i]]){
                    dp[j]=1;
                }
            }
        }
        int temp=-1;
        for(int i=0;i<mx;i++){
            if(dp[i]){
                temp=i;
            }
        }
        return mx+temp;
    }
};
