class Solution {
public:
    int maxTotalReward(vector<int>& arr) {
        sort(arr.begin(),arr.end());
        arr.erase(unique(arr.begin(),arr.end()),arr.end());
        int n=arr.size();
        const int N=100005;
        int num=100000;
        bitset<N> dp,dp2;
        dp[num]=1;
        for(int i=0;i<n;i++){
            dp2=dp;
            dp2=dp2>>(num-arr[i]+1);
            dp2=dp2<<(num-arr[i]+1);
            dp2=dp2>>(arr[i]);
            dp|=dp2;
        }
        int res=0;
        for(int i=0;i<=num;i++){
            if(dp[i])   res=max(res,num-i);
        }
        return res;
    }
};
