bitset<100001> ones, zeros;

class Solution {
public:
    int maxTotalReward(vector<int>& a) {
        if (ones[0] == 0) {
            for (int i=0; i<=100000; ++i) ones[i] = 1;
        }
        sort(a.begin(), a.end());
        const int n = a.size();
        bitset<100001> dp;
        dp[0] = 1;
        int mx = 0;
        int filled = 0;
        for (int i=0; i<n; ++i) {
            int j = a[i] - 1;
            if (j + a[i] > filled) {
                bitset<100001> mask = ones ^ (ones << (j + 1));
                dp = dp | ((dp & mask) << a[i]);
            }
            while (filled < 100000 && dp[filled]) ++filled;
        }
        for (int i=0; i<=100000; ++i) if (dp[i]) mx = max(mx, i);
        return mx;
    }
};
