#include <iostream>
#define ll long long

using namespace std;

class Solution {
public:
    int maxTotalReward(vector<int>& rewardValues) {
        ll n = rewardValues.size();
        bitset<100001> dp;
        dp[0] = 1;
        sort(rewardValues.begin(), rewardValues.end());
        for (auto u : rewardValues) {
            dp |= (dp << (100001-u)) >> (100001-2*u);
            //dp |= dp << u;
        }
        for (int i=100000; i>=0; --i) {
            if (dp[i]) return i;
        }
        return 0;
    }
};
