class Solution {
public:
    int maxTotalReward(vector<int>& rewardValues) {
        int n = sz(rewardValues);
        int ret = 0;
        sort(all(rewardValues));
        const int N = 1e5 + 5;
        bitset<N> dp;
        dp[0] = 1;

        for (auto x : rewardValues) {
            bitset<N> k = bitset<N>().set();
            k >>= N - x;
            bitset<N> mask(dp);
            mask &= k;
            mask <<= x;
            dp |= mask;
        }

        fori(N + 1) {
            if (dp[i])
                ret = i;
        }
        return ret;
    }
};
