class Solution {
public:
int maxTotalReward(vector<int>& rewardValues) {
    sort(rewardValues.begin(), rewardValues.end());
    int m = rewardValues.back() + 1;
    bitset<50001> bs;
    bitset<50001> tmp;
    int cur = 0;
    bs.set(0, 1);
    for (int i = 0; i < rewardValues.size()-1; i++) {
        int num = rewardValues[i];
        while (cur < num) {
            tmp.set(cur, 1);
            cur++;
        }
        bs |= (bs & tmp) << num;
    }
    for (int i = 50000; i >= 0; i--) {
        if (bs[i] && i<rewardValues.back())
            return i+ rewardValues.back();
    }
    return 0;
}
};
