class Solution {
public:
    int maxTotalReward(vector<int>& rewardValues) {
        const int MAX_SUM = 1E5 + 5;  
        bitset<MAX_SUM> mainBitset;

        mainBitset.reset();
        mainBitset[0] = 1;  


        sort(rewardValues.begin(), rewardValues.end());


        for(int num : rewardValues) {
            mainBitset = (((mainBitset << (1E5 + 5 - num)) >> (1E5 + 5 - num)) << num) | mainBitset;
        }


        for(int i = MAX_SUM - 1; i >= 0; i--) {
            if(mainBitset[i]) {
                return i;
            }
        }
        return 1;
    }
};
