class Solution {
public:
    int func(int sum,vector<int> &a,vector<int> &v){
        if(v[sum]!=-1) return v[sum]; 
        int j=lower_bound(a.begin(),a.end(),sum)-a.begin();
        if(j<a.size() && a[j]==sum) return v[sum]=1;
        for(int i=j-1;i>=0 && 2*a[i]>sum;i--)
            if(func(sum-a[i],a,v)) return v[sum]=1;
        return v[sum]=0;
    }

    int maxTotalReward(vector<int>& rewardValues) {
        set<int> s;
        vector<int> a;
        for(auto e:rewardValues) s.insert(e); 
        for(auto e:s) a.push_back(e); 
        int n=a.size();
        int N=a[n-1]+4;
        vector<int> v(N,-1);
        v[0]=1;
        int ans=a[n-1];
        // for(auto e:a) cout<<e<<" "; cout<<endl;
        for(int i=a[n-1]-1;i>=1;i--)
            if(func(i,a,v)) return ans+i;
        return ans;
    }
};
