class Solution {
public:
    int maxTotalReward(vector<int>& r) {
        int n = r.size();
        sort(r.begin(), r.end());
        // r[i] > x
        // x' = r[i] + x
        // x = x' - r[i] < r[i]
        // x' < 2*r[i]
        // x'/2 <= r[i] < x'
        vector<bool> dp(r.back()+1, 0);
        dp[0] = 1;
        for(int i = 0; i < n; i++) {
            dp[r[i]] = 1;
        }
        int ans = 0;
        for(int x = 1, j = 0; x < r.back(); x++) {
            if(dp[x]) {
                ans = x;
                continue;
            }
            while(j < n && 2*r[j] <= x) j++;
            for(int i = j; i < n && r[i] < x; i++) {
                if(dp[x - r[i]]) {
                    dp[x] = 1;
                    ans = x;
                    break;
                }
            }
            // cout << x << " " << dp[x] << " " << j << "\n";
        }
        return ans + r.back();
    }
};
