class Solution:
    def maxTotalReward(self, a: List[int]) -> int:

        a.sort()
        n = len(a)

        dp = 1
        for i in range(n):
            l_mask = ((1 << a[i]) - 1) & dp
            dp = dp | (l_mask << a[i])

        for sz in range(100000 , -1 , -1):
            if(dp >> sz & 1):
                return sz
