class Solution:
    def maxTotalReward(self, r: List[int]) -> int:
        @cache
        def dfs(i: int, v: int) -> bool:
            if v == 0:
                return True
            if i < 0:
                return False
            for j in range(i + 1):
                if r[j] > v:
                    break
                if v - r[j] >= r[j]:
                    continue
                if dfs(j - 1, v - r[j]):
                    return True
            return False

        r = list(set(r))
        r.sort()
        n = len(r)
        for x in range(r[-1] - 1, -1, -1):
            if dfs(n - 2, x):
                return x + r[-1]
