// Important header files   
#include <ext/pb_ds/assoc_container.hpp> // Common file  
#include <ext/pb_ds/tree_policy.hpp>  
#include <functional> // for less  
#include <iostream>  
using namespace __gnu_pbds;  
using namespace std;  

// Declaring ordered_set 
typedef tree<int, null_type, less<int>, rb_tree_tag,  
            tree_order_statistics_node_update>  
    ordered_set;  


class Solution {
public:
    vector<int> countOfPeaks(vector<int>& nums, vector<vector<int>>& qu) {
        int n=nums.size();
        ordered_set s; 
        for(int i=1;i<n-1;i++){
            if(nums[i]>nums[i-1] and nums[i]>nums[i+1]){
                s.insert(i);
            }
        }
        int q=qu.size();
        vector<int> ans;
        for(int i=0;i<q;i++){
            // cout<<i<<" ";
            if(qu[i][0]==1){
                int x=s.order_of_key(qu[i][2])-s.order_of_key(qu[i][1]+1);
                if(x<0){
                    x=0;
                }
                ans.push_back(x);
            }else{
                if(s.find(qu[i][1]-1)!=s.end()){
                    s.erase(qu[i][1]-1);
                }
                // cout<<1<<" ";
                if(s.find(qu[i][1]+1)!=s.end()){
                    s.erase(qu[i][1]+1);
                }
                // cout<<2<<" ";
                if(s.find(qu[i][1])!=s.end()){
                    s.erase(qu[i][1]);
                }
                // cout<<3<<" ";
                nums[qu[i][1]]=qu[i][2];
                int st=max(1,qu[i][1]-1),en=min(n-2,qu[i][1]+1);
                // cout<<st<<" "<<en<<endl;
                for(int j=st;j<=en;j++){
                    if(nums[j]>nums[j-1] and nums[j]>nums[j+1]){

                        s.insert(j);
                    }
                }
            }
        }
        return ans;
    }
};
