from typing import List

class SegmentTree:
    def __init__(self, n):
        self.n = n
        self.tree = [0] * (4 * n)

    def build(self, arr, start, end, node):
        if start == end:
            self.tree[node] = arr[start]
        else:
            mid = (start + end) // 2
            self.build(arr, start, mid, 2 * node + 1)
            self.build(arr, mid + 1, end, 2 * node + 2)
            self.tree[node] = self.tree[2 * node + 1] + self.tree[2 * node + 2]

    def update(self, start, end, idx, val, node):
        if start == end:
            self.tree[node] = val
        else:
            mid = (start + end) // 2
            if start <= idx <= mid:
                self.update(start, mid, idx, val, 2 * node + 1)
            else:
                self.update(mid + 1, end, idx, val, 2 * node + 2)
            self.tree[node] = self.tree[2 * node + 1] + self.tree[2 * node + 2]

    def query(self, start, end, l, r, node):
        if r < start or end < l:
            return 0
        if l <= start and end <= r:
            return self.tree[node]
        mid = (start + end) // 2
        left_query = self.query(start, mid, l, r, 2 * node + 1)
        right_query = self.query(mid + 1, end, l, r, 2 * node + 2)
        return left_query + right_query

class Solution:
    def countOfPeaks(self, nums: List[int], queries: List[List[int]]) -> List[int]:
        n = len(nums)

        # Function to determine if an index is a peak
        def is_peak(index):
            if index <= 0 or index >= n - 1:
                return False
            return nums[index] > nums[index - 1] and nums[index] > nums[index + 1]

        # Initialize the peaks array
        peaks = [1 if is_peak(i) else 0 for i in range(n)]

        # Create the segment tree
        seg_tree = SegmentTree(n)
        seg_tree.build(peaks, 0, n - 1, 0)

        results = []

        for query in queries:
            if query[0] == 1:
                li, ri = query[1], query[2]
                if li == ri or li + 1 == ri:
                    results.append(0)
                else:
                    results.append(seg_tree.query(0, n - 1, li + 1, ri - 1, 0))
            elif query[0] == 2:
                indexi, vali = query[1], query[2]
                nums[indexi] = vali
                for i in range(max(1, indexi - 1), min(n - 1, indexi + 2)):
                    seg_tree.update(0, n - 1, i, 1 if is_peak(i) else 0, 0)

        return results

