class SegmentTree:
    def __init__(self, data):
        self.n = len(data)
        self.tree = [0] * (2 * self.n)
        self.build(data)

    def build(self, data):
        for i in range(self.n):
            self.tree[self.n + i] = data[i]
        for i in range(self.n - 1, 0, -1):
            self.tree[i] = self.tree[i * 2] + self.tree[i * 2 + 1]

    def update(self, pos, value):
        pos += self.n
        self.tree[pos] = value
        while pos > 1:
            pos //= 2
            self.tree[pos] = self.tree[2 * pos] + self.tree[2 * pos + 1]

    def query(self, left, right):
        result = 0
        left += self.n
        right += self.n
        while left < right:
            if left % 2:
                result += self.tree[left]
                left += 1
            if right % 2:
                right -= 1
                result += self.tree[right]
            left //= 2
            right //= 2
        return result
class Solution:
    def countOfPeaks(self, nums: List[int], queries: List[List[int]]) -> List[int]:
        def is_peak(nums, i):
            if i == 0 or i == len(nums) - 1:
                return False
            return nums[i] > nums[i - 1] and nums[i] > nums[i + 1]


        n = len(nums)
        is_peak_array = [0] * n
        for i in range(1, n - 1):
            is_peak_array[i] = 1 if is_peak(nums, i) else 0

        seg_tree = SegmentTree(is_peak_array)
        res = []

        for query in queries:
            if query[0] == 1:
                firsttype, li, ri = query
                res.append(seg_tree.query(li + 1, ri))
            elif query[0] == 2:
                secondtype, index, val = query
                nums[index] = val
                for i in range(max(1, index - 1), min(n - 1, index + 2)):
                    seg_tree.update(i, 1 if is_peak(nums, i) else 0)
        return res
