class segment{
public:
    vector<int> seg,lazy;
    int n;
    segment(vector<int> &a){
        this->n = a.size();
        seg.resize(4*n);
        lazy.resize(4*n);
        build(a,0,0,n-1);
    }

    void build(vector<int> &a,int idx,int left,int right){
        if(left == right){
            seg[idx] = a[left];
            return;
        }
        int mid = (left+right)/2;
        build(a,2*idx+1,left,mid);
        build(a,2*idx+2,mid+1,right);
        seg[idx] = seg[2*idx+1] + seg[2*idx+2];
    }


    int query(int l,int r,int idx = 0,int left = 0,int right = -1){
        if(right == -1)right = n-1;
        if(left>r || right < l)return 0;
        if(left>=l && right<=r)return seg[idx];
        int mid = (left + right)/2;
        return (query(l,r,2*idx+1,left,mid) + query(l,r,2*idx+2,mid+1,right));
    }

    void update(int pos,int val,int idx = 0,int left = 0,int right = -1){
        if(right == -1)right = n-1;
        if(left == right){
            seg[idx] = val;
            return;
        }
        int mid = (left + right)/2;
        if(pos <= mid) update(pos,val,idx*2+1,left,mid);
        else update(pos,val,idx*2+2,mid+1,right);
        seg[idx] = seg[2*idx+1] + seg[2*idx+2];
    }

};
class Solution {
public:
    vector<int> countOfPeaks(vector<int>& nums, vector<vector<int>>& queries) {
        int n=nums.size();
        vector<int> hill(n,0);
        for(int i=1;i<n-1;i++){
            if(nums[i]>nums[i-1] && nums[i]>nums[i+1]){
                hill[i]=1;
            }
        }
        segment a(hill);
        vector<int> ans;
        for(auto qr:queries){
            if(qr[0]==1){
                int l=qr[1];
                int r=qr[2];
                l++;
                r--;
                ans.push_back(a.query(l,r));

            }
            else{
                int ind=qr[1];
                int val=qr[2];
                if(nums[ind]>val){
                    if(ind>=2 && ind<=n-3){
                        if(val>nums[ind-1] && val>nums[ind+1]){
                            nums[ind]=val;
                        }
                        else{
                            hill[ind]=0;
                            nums[ind]=val;
                            a.update(ind,0);
                            if(nums[ind-1]>nums[ind-2] && nums[ind-1]>val){
                                hill[ind-1]=1;
                                a.update(ind-1,1);
                            }
                            if(nums[ind+1]>nums[ind+2] && nums[ind+1]>val){
                                hill[ind+1]=1;
                                a.update(ind+1,1);
                            }
                        }
                    }
                    else if(ind==1){
                        if(val>nums[ind-1] && val>nums[ind+1]){
                            nums[ind]=val;
                        }
                        else{
                            hill[ind]=0;
                            a.update(ind,0);
                            nums[ind]=val;
                            if(nums[ind+1]>nums[ind+2] && nums[ind+1]>val){
                                hill[ind+1]=1;
                                a.update(ind+1,1);
                            }
                        }
                    }
                    else if(ind==n-2){
                        if(val>nums[ind-1] && val>nums[ind+1]){
                            nums[ind]=val;
                        }
                        else{
                            hill[ind]=0;
                            a.update(ind,0);
                            nums[ind]=val;
                            if(nums[ind-1]>nums[ind-2] && nums[ind-1]>val){
                                hill[ind-1]=1;
                                a.update(ind-1,1);
                            }
                        }
                    }
                    else if(ind==0){
                        nums[ind]=val;
                        if(nums[ind+1]>nums[ind+2] && nums[ind+1]>val){
                                hill[ind+1]=1;
                                a.update(ind+1,1);
                            }

                    }
                    else if(ind==n-1){
                        nums[ind]=val;
                        if(nums[ind-1]>nums[ind-2] && nums[ind-1]>val){
                                hill[ind-1]=1;
                                a.update(ind-1,1);
                        }
                    }
                }
                else if(nums[ind]<val){
                    if(ind!=0 && ind!=n-1){
                        if(val>nums[ind-1] && val>nums[ind+1]){
                            hill[ind]=1;
                            nums[ind]=val;
                            a.update(ind,1);
                            hill[ind-1]=0;
                            hill[ind+1]=0;
                            a.update(ind-1,0);
                            a.update(ind+1,0);
                        }
                        else{
                            hill[ind]=0;
                            nums[ind]=val;
                            a.update(ind,0);
                            if(nums[ind-1]<=val){
                                hill[ind-1]=0;
                                a.update(ind-1,0);

                            }
                            if(nums[ind+1]<=val){
                                hill[ind+1]=0;
                                a.update(ind+1,0);
                            }
                        }
                    }
                    else if(ind==n-1){
                        hill[ind]=0;
                            nums[ind]=val;
                            // a.update(ind,0);
                            if(nums[ind-1]<=val){
                                hill[ind-1]=0;
                                a.update(ind-1,0);

                            }
                        }
                    else if(ind==0){
                        hill[ind]=0;
                            nums[ind]=val;
                            // a.update(ind,0);
                            if(nums[ind+1]<=val){
                                hill[ind+1]=0;
                                a.update(ind+1,0);
                            }
                    }

                }
            }   
        }
        return ans;
    }
};
