#include <vector>
#include <iostream>
using namespace std;

class SegmentTree {
private:
    vector<int> nums;
    vector<int> tree;
    int n;

    bool isPeak(int index) {
        if (index <= 0 || index >= n - 1) return false;
        return nums[index] > nums[index - 1] && nums[index] > nums[index + 1];
    }

    void build(int node, int start, int end) {
        if (start == end) {
            tree[node] = isPeak(start) ? 1 : 0;
        } else {
            int mid = (start + end) / 2;
            build(2 * node + 1, start, mid);
            build(2 * node + 2, mid + 1, end);
            tree[node] = tree[2 * node + 1] + tree[2 * node + 2];
        }
    }

    void update(int node, int start, int end, int idx) {
        if (start == end) {
            tree[node] = isPeak(start) ? 1 : 0;
        } else {
            int mid = (start + end) / 2;
            if (start <= idx && idx <= mid) {
                update(2 * node + 1, start, mid, idx);
            } else {
                update(2 * node + 2, mid + 1, end, idx);
            }
            tree[node] = tree[2 * node + 1] + tree[2 * node + 2];
        }
    }

    int query(int node, int start, int end, int L, int R) {
        if (R < start || L > end) {
            return 0;
        }
        if (L <= start && end <= R) {
            return tree[node];
        }
        int mid = (start + end) / 2;
        int left_count = query(2 * node + 1, start, mid, L, R);
        int right_count = query(2 * node + 2, mid + 1, end, L, R);
        return left_count + right_count;
    }

public:
    SegmentTree(const vector<int>& nums) : nums(nums) {
        n = nums.size();
        tree.resize(4 * n);
        build(0, 0, n - 1);
    }

    void update(int idx, int val) {
        nums[idx] = val;
        for (int i = max(1, idx - 1); i <= min(n - 2, idx + 1); ++i) {
            update(0, 0, n - 1, i);
        }
    }

    int query(int L, int R) {
        if (L == R) return 0;
        if (L == R - 1) return 0;
        return query(0, 0, n - 1, L + 1, R - 1);
    }
};

class Solution {
public:
    vector<int> countOfPeaks(vector<int>& nums, vector<vector<int>>& queries) {
        SegmentTree st(nums);
        vector<int> result;
        for (const auto& q : queries) {
            if (q[0] == 1) {
                result.push_back(st.query(q[1], q[2]));
            } else if (q[0] == 2) {
                st.update(q[1], q[2]);
            }
        }
        return result;
    }
};
