class SegmentTree {
private:
    vector<int> tree;
    vector<int> nums;
    int n;

    bool isPeak(int i) {
        if (i <= 0 || i >= nums.size() - 1) return false;
        return nums[i] > nums[i - 1] && nums[i] > nums[i + 1];
    }

    void build(int node, int start, int end) {
        if (start == end) {
            tree[node] = isPeak(start) ? 1 : 0;
        } else {
            int mid = (start + end) / 2;
            build(2 * node + 1, start, mid);
            build(2 * node + 2, mid + 1, end);
            tree[node] = tree[2 * node + 1] + tree[2 * node + 2];
        }
    }

    void updateTree(int node, int start, int end, int idx) {
        if (start == end) {
            tree[node] = isPeak(start) ? 1 : 0;
        } else {
            int mid = (start + end) / 2;
            if (start <= idx && idx <= mid) {
                updateTree(2 * node + 1, start, mid, idx);
            } else {
                updateTree(2 * node + 2, mid + 1, end, idx);
            }
            tree[node] = tree[2 * node + 1] + tree[2 * node + 2];
        }
    }

    int queryTree(int node, int start, int end, int L, int R) {
        if (R < start || end < L) {
            return 0;
        }
        if (L <= start && end <= R) {
            return tree[node];
        }
        int mid = (start + end) / 2;
        int leftCount = queryTree(2 * node + 1, start, mid, L, R);
        int rightCount = queryTree(2 * node + 2, mid + 1, end, L, R);
        return leftCount + rightCount;
    }

public:
    SegmentTree(vector<int>& nums) : nums(nums) {
        n = nums.size();
        tree.resize(4 * n);
        build(0, 0, n - 1);
    }

    void update(int idx, int val) {
        nums[idx] = val;
        updateTree(0, 0, n - 1, idx);
        if (idx > 0) {
            updateTree(0, 0, n - 1, idx - 1);
        }
        if (idx < n - 1) {
            updateTree(0, 0, n - 1, idx + 1);
        }
    }

    int query(int L, int R) {
        if (L >= R - 1) return 0;
        return queryTree(0, 0, n - 1, L + 1, R - 1);
    }
};

class Solution {
public:
    vector<int> countOfPeaks(vector<int>& nums, vector<vector<int>>& queries) {
        SegmentTree segTree(nums);
        vector<int> result;

        for (const auto& query : queries) {
            if (query[0] == 1) {
                int L = query[1];
                int R = query[2];
                result.push_back(segTree.query(L, R));
            } else if (query[0] == 2) {
                int index = query[1];
                int value = query[2];
                segTree.update(index, value);
            }
        }

        return result;
    }
};
