class Solution {

struct Fenwick
{
    vector<long long> BIT;
    long long MAXN;

    void init(int n)
    {
        BIT.resize(n+5);
        MAXN=n+5;
    }

    long long query(long long x)
    {
        x++;
       long long ans=0;
       while(x>0)
       {
          ans+=BIT[x];
          x-=(x&-x);
       }
       return ans; 
    }

    void update(long long x,long long val)
    {
        x++;
       while(x<MAXN)
       {
          BIT[x]+=val;
          x+=(x&-x);
       }
    }

};

public:
    vector<int> countOfPeaks(vector<int>& nums, vector<vector<int>>& queries) {
        int n=nums.size();
        Fenwick ft;
        ft.init(n+1);
        vector<int> status(n);
        for(int i=1;i<n-1;i++)
        {
            if(nums[i]>nums[i-1] && nums[i]>nums[i+1])
            {
                status[i]=1;
                ft.update(i,1);
            }
        }

        vector<int> ans;
        for(auto query : queries)
        {
            int type=query[0];
            if(type==1)
            {
                int l=query[1];
                int r=query[2];
                if(l==r)
                    ans.push_back(0);
                else
                    ans.push_back(ft.query(r-1)-ft.query(l));
            }
            else
            {
                int ind=query[1];
                int val=query[2];
                for(int i=-1;i<=1;i++)
                {
                    if(ind+i>=0 && ind+i<n)
                        ft.update(ind+i,-status[ind+i]);
                }

                nums[ind]=val;
                for(int i=-1;i<=1;i++)
                {
                    int j=ind+i;
                    if(j>=1 && j<n-1)
                    {
                        if(nums[j]>nums[j-1] && nums[j]>nums[j+1])
                        {
                            status[j]=1;
                            ft.update(j,1);
                        }
                        else
                            status[j]=0;
                    }
                }
            }
        }
        return ans;
    }
};
