class Solution {
private:
    int n;
    int a[100005],c[100005]; //对应原数组和树状数组

int lowbit(int x){
    return x&(-x);
}

void updata(int i,int k){    //在i位置加上k
    a[i]+=k;
    while(i <= n){
        c[i] += k;
        i += lowbit(i);
    }
}

int getsum(int i){        //求A[1 - i]的和
    int res = 0;
    while(i > 0){
        res += c[i];
        i -= lowbit(i);
    }
    return res;
}

public:
    vector<int> countOfPeaks(vector<int>& nums, vector<vector<int>>& queries) {
        n=nums.size();
        memset(a,0,sizeof(a));
        memset(c,0,sizeof(c));
        for(int i=1;i<n-1;i++){
            if(nums[i-1]<nums[i]&&nums[i]>nums[i+1])updata(i,1);
        }
        vector<int> res;
        function<void(int)> check=[&](int i){
            if(i<=0||i>=n-1)return;
            int k=(nums[i-1]<nums[i]&&nums[i]>nums[i+1])-a[i];
            if(k)updata(i,k);
        };
        for(auto& q:queries){
            int& op=q[0];
            if(op==1){
                int &l=q[1],&r=q[2];
                res.emplace_back(r>l?getsum(r-1)-getsum(l):0);
            }else{
                int &idx=q[1],&val=q[2];
                nums[idx]=val;
                check(idx);
                check(idx-1);
                check(idx+1);
            }
        }
        return res;
    }
};
