from sortedcontainers import SortedSet as SS

class Solution:
  def countOfPeaks(self, nums: List[int], queries: List[List[int]]) -> List[int]:
    ss = SS()
    n = len(nums)
    res = []
    for i in range(1, n - 1):
      if nums[i] > max(nums[i - 1], nums[i + 1]):
        ss.add(i)
    for t, i, j in queries:
      if t == 1:
        res.append(max(0, ss.bisect_right(j - 1) - ss.bisect_left(i + 1)))
      else:
        nums[i] = j
        for k in range(i - 1, i + 2):
          if 0 < k < n - 1 and nums[k] > max(nums[k - 1], nums[k + 1]):
            ss.add(k)
          else:
            ss.discard(k)
    return res
