class TreeNode:
    def __init__(self):
        self.val = 0
        self.chd = collections.defaultdict(TreeNode)

class Solution:
    def countOfPeaks(self, nums: List[int], queries: List[List[int]]) -> List[int]:
        n = len(nums)
        MAX_BITS = 17

        status = [0] + [1 if nums[i] > nums[i - 1] and nums[i] > nums[i + 1] else 0 for i in range(1, n - 1)] + [0]
        print(status)

        root = TreeNode()

        def update_val(ind: int, dv: int) -> None:
            #print(f"Updating the value at index {ind} by an increment of {dv}.")
            ptr = root
            for bit in range(MAX_BITS - 1, -1, -1):
                if ind & (1 << bit):
                    ptr = ptr.chd[bit]
                else:
                    ptr.chd[bit].val += dv
                    #print("Written", bit, dv)

        def get_sum(ind: int) -> None:
            #print(f"Retrieving the sum of values up to index {ind}.")
            ptr = root
            ret = 0
            for bit in range(MAX_BITS - 1, -1, -1):
                if ind & (1 << bit):
                    ptr = ptr.chd[bit]
                    ret += ptr.val
                    #print("Obtained", bit, ptr.val)

            return ret

        for i in range(1, n -1):
            if status[i]:
                update_val(i, status[i])

        ans = []
        for type_, u, v in queries:
            if type_ == 2:
                nums[u] = v
                for i in range(u - 1, u + 2):
                    if 1 <= i <= n - 2:
                        orig_status = status[i]
                        status[i] = 1 if nums[i] > nums[i - 1] and nums[i] > nums[i + 1] else 0
                        update_val(i, status[i] - orig_status)
                #print(status)
            else:
                if v - u < 2:
                    ans.append(0)
                else:
                    ans.append(get_sum(v) - get_sum(u + 1))

        return ans
