class SegmentTree {
private:
    vector<int> segtree;
    vector<int> nums;
    int n;

    bool isPeak(int idx) {
        if (idx <= 0 || idx >= n - 1) return false;
        return nums[idx] > nums[idx - 1] && nums[idx] > nums[idx + 1];
    }

    void build(int idx, int l, int r) {
        if (l == r) {
            segtree[idx] = isPeak(l) ? 1 : 0;
            return;
        }
        int mid = l + (r - l) / 2;
        build(2 * idx + 1, l, mid);
        build(2 * idx + 2, mid + 1, r);
        segtree[idx] = segtree[2 * idx + 1] + segtree[2 * idx + 2];
    }

    void update(int idx, int l, int r, int pos) {
        if (l == r) {
            segtree[idx] = isPeak(l) ? 1 : 0;
            return;
        }
        int mid = l + (r - l) / 2;
        if (pos <= mid) {
            update(2 * idx + 1, l, mid, pos);
        } else {
            update(2 * idx + 2, mid + 1, r, pos);
        }
        segtree[idx] = segtree[2 * idx + 1] + segtree[2 * idx + 2];
    }

    int query(int idx, int l, int r, int ql, int qr) {
        if (ql > r || qr < l) return 0;
        if (ql <= l && r <= qr) return segtree[idx];
        int mid = l + (r - l) / 2;
        return query(2 * idx + 1, l, mid, ql, qr) + query(2 * idx + 2, mid + 1, r, ql, qr);
    }

public:
    SegmentTree(vector<int>& arr) : nums(arr) {
        n = arr.size();
        segtree.resize(4 * n, 0);
        build(0, 0, n - 1);
    }

    void update(int pos, int val) {
        nums[pos] = val;
        if (pos > 0) update(0, 0, n - 1, pos - 1);
        update(0, 0, n - 1, pos);
        if (pos < n - 1) update(0, 0, n - 1, pos + 1);
    }

    int query(int ql, int qr) {
        if (ql == qr) return 0;
        return query(0, 0, n - 1, ql + 1, qr - 1);
    }
};

class Solution {
public:
    vector<int> countOfPeaks(vector<int>& nums, vector<vector<int>>& queries) {
        SegmentTree s(nums);
        vector<int> ans;

        for (auto & q : queries) {
            if (q[0] == 1) {
                ans.push_back(s.query(q[1], q[2]));
            } else if (q[0] == 2) {
                s.update(q[1], q[2]);
            }
        }

        return ans;
    }
};
